CREATE TABLE IF NOT EXISTS books7
{
 ISBN text,
 book_title text,   
}

SELECT publisher, COUNT(*) as book_counts
  FROM books.publishers_view
  GROUP BY publisher;

SELECT publisher,ISBN COUNT(*) as book_counts
  FROM books.publishers_view
  GROUP BY publisher;

SELECT publisher,, COUNT(*) as book_counts
  FROM books.publishers_view
  GROUP BY publisher;

SELECT publisher,ISBN,  COUNT(*) as book_counts
  FROM books.publishers_view
  GROUP BY publisher;

SELECT publisher
  FROM books.publishers_view
  GROUP BY publisher;

SELECT publisher
  FROM books.publishers_view
  GROUP BY (publisher);

SELECT publisher, COUNT(*) as book_counts
FROM books.publishers_view
GROUP BY publisher;

